/**
 * Anchor instruction discriminators
 * These are generated by Anchor from SHA256("global:instruction_name")
 * Extracted from the IDL files - these values are deterministic and won't change
 * unless the instruction names in the Rust code change.
 */

/**
 * Identity Registry instruction discriminators
 */
export const IDENTITY_DISCRIMINATORS = {
  initialize: Buffer.from([0xaf, 0xaf, 0x6d, 0x1f, 0x0d, 0x98, 0x9b, 0xed]),
  register: Buffer.from([0xd3, 0x7c, 0x43, 0x0f, 0xd3, 0xc2, 0xb2, 0xf0]),
  setAgentUri: Buffer.from([0x2b, 0xfe, 0xa8, 0x68, 0xc0, 0x33, 0x27, 0x2e]),
  setMetadata: Buffer.from([0x4e, 0x9d, 0x4b, 0xf2, 0x97, 0x14, 0x79, 0x90]),
  transferAgent: Buffer.from([0x89, 0x50, 0x38, 0x93, 0x6b, 0x63, 0x27, 0xc0]),
  syncOwner: Buffer.from([0x2e, 0x05, 0xe8, 0xc6, 0x3b, 0x9e, 0xa0, 0x77]),
} as const;

